function getDigitCount(numb){
    let counter = 1;
    let tempVal = numb;

    while(Math.abs(tempVal/10) >= 1){
        counter++
        tempVal = tempVal/10
    }
    return counter
}

function getDigitCountAlt(numb){
    if(numb == 0){
        return 1
    }
    return Math.floor(Math.log10(Math.abs(numb)))+1
}



